import React, { useState } from 'react';

const CustomDock = () => {
    // State to manage the visibility of the tab
    const [isTabVisible, setIsTabVisible] = useState(false);

    // Toggle visibility when the image is clicked
    const handleClick = () => {
        setIsTabVisible(!isTabVisible);
    };

    return (
        <div className='mx-auto min-h-screen flex flex-col items-center justify-center py-20 max-md:py-16 px-8 bg-gray-300'>
            <div className='flex gap-4 bg-gray-900 w-full rounded-t-2xl fixed -bottom-5 max-w-[1140px]'>
                <img className='w-full rounded-2xl hover:scale-110 finder transition-all duration-300 h-full max-w-[80px] cursor-pointer -translate-y-8' src="/assets/images/png/finder.png" alt="img"
                    onClick={handleClick}
                />
            </div>
            {isTabVisible && (
                <div className='fixed inset-0 bg-black/90 flex justify-center items-center z-50 px-5'>
                    <div className="max-w-[500px] overflow-hidden rounded-2xl max-h-[600px] bg-white">
                        <h2 className="text-center text-white py-4">Your Tab Content</h2>
                        <p className="text-white text-center">Here is some content inside the tab!</p>
                    </div>
                </div>
            )}
        </div>
    );
}

export default CustomDock;
